import React from 'react';
import Card from './components/Card';
import Form from './components/Form';

const stateZero = {
  name: '',
  description: '',
  attr01: 0,
  attr02: 0,
  attr03: 0,
  imageCard: '',
  rareCardSelect: 'normal',
  checkBoxTryunfo: false,
  buttonDisable: true,
  // hasTrunfo: false,
};

class App extends React.Component {
  constructor() {
    super();
    this.state = stateZero;
  }

  handleChange = (event) => {
    const { name, value, checked, type } = event.target;
    this.setState({
      [name]: type === 'checkbox' ? checked : value,
    }, this.buttonIsCheck);
  };

  buttonIsCheck = () => {
    const {
      name,
      description,
      attr01,
      attr02,
      attr03,
      imageCard,
    } = this.state;

    const maxForAttr = 90;
    const maxForTotal = 210;
    const takingTotalSum = parseInt(attr01, 10)
      + parseInt(attr02, 10) + parseInt(attr03, 10);

    let isDisabled = false;
    if (name === '') {
      isDisabled = true;
    }
    if (description === '') {
      isDisabled = true;
    }
    if (parseInt(attr01, 10) > maxForAttr || parseInt(attr01, 10) < 0) {
      isDisabled = true;
    }
    if (parseInt(attr02, 10) > maxForAttr || parseInt(attr02, 10) < 0) {
      isDisabled = true;
    }
    if (parseInt(attr03, 10) > maxForAttr || parseInt(attr03, 10) < 0) {
      isDisabled = true;
    }
    if (takingTotalSum > maxForTotal) {
      isDisabled = true;
    }
    if (imageCard === '') {
      isDisabled = true;
    }

    this.setState({ buttonDisable: isDisabled });
  };

  render() {
    const {
      name,
      description,
      attr01,
      attr02,
      attr03,
      imageCard,
      rareCardSelect,
      checkBoxTryunfo,
      buttonDisable,
      // hasTrunfo,
    } = this.state;
    return (
      <>
        <Form
          cardName={ name }
          cardDescription={ description }
          cardAttr1={ attr01 }
          cardAttr2={ attr02 }
          cardAttr3={ attr03 }
          cardImage={ imageCard }
          cardRare={ rareCardSelect }
          cardTrunfo={ checkBoxTryunfo }
          isSaveButtonDisabled={ buttonDisable }
          onInputChange={ this.handleChange }
        />
        <Card
          cardName={ name }
          cardDescription={ description }
          cardAttr1={ attr01 }
          cardAttr2={ attr02 }
          cardAttr3={ attr03 }
          cardImage={ imageCard }
          cardRare={ rareCardSelect }
          cardTrunfo={ checkBoxTryunfo }
        />
      </>
    );
  }
}
export default App;
