import React, { Component } from 'react';
import PropTypes from 'prop-types';

class CardRender extends Component {
  render() {
    const { arrCards } = this.props;
    const cardsToShow = () => {
      const passandoMap = arrCards.map((card, index) => (
        <div key={ index }>
          <p id={ card.cardName } data-testid="name-card">{ card.cardName }</p>
          <div>
            <img
              src={ card.cardImage }
              alt={ card.cardName }
              data-testid="image-card"
            />
          </div>
          <p data-testid="description-card">{ card.cardDescription }</p>
          <p data-testid="attr1-card">{ card.cardAttr1 }</p>
          <p data-testid="attr2-card">{ card.cardAttr2 }</p>
          <p data-testid="attr3-card">{ card.cardAttr3 }</p>
          <p data-testid="rare-card">{ card.cardRare }</p>
          <p>{ card.cardTrunfo }</p>
        </div>
      ));
      return passandoMap;
    };
    return (
      <div>
        { cardsToShow() }
      </div>
    );
  }
}

CardRender.propTypes = {
  arrCards: PropTypes.arrayOf(PropTypes.shape({
    cardName: PropTypes.string,
    cardDescription: PropTypes.string,
    cardAttr1: PropTypes.number,
    cardAttr2: PropTypes.number,
    cardAttr3: PropTypes.number,
    cardImage: PropTypes.string,
    cardRare: PropTypes.string,
    cardTrunfo: PropTypes.bool,
  })).isRequired,
};
export default CardRender;
