import React from 'react';
import Card from './components/Card';
import Form from './components/Form';

const stateZero = {
  name: '',
  description: '',
  attr1: 0,
  attr2: 0,
  attr3: 0,
  image: '',
  rare: 'normal',
  isTrunfo: false,
  save: true,
  listCard: [],
  hasTrunfo: false,
};

class App extends React.Component {
  constructor() {
    super();
    this.state = stateZero;
  }

  handleChange = (event) => {
    const { name, value, checked, type } = event.target;
    this.setState({
      [name]: type === 'checkbox' ? checked : value,
    });
  };

  render() {
    const {
      name,
      description,
      attr01,
      attr02,
      attr03,
      imageCard,
      rareCardSelect,
      isTrunfo,
      // save,
      // hasTrunfo,
      // listCard,
    } = this.state;
    return (
      <>
        <Form
          cardName={ name }
          cardDescription={ description }
          cardAttr1={ attr01 }
          cardAttr2={ attr02 }
          cardAttr3={ attr03 }
          cardImage={ imageCard }
          cardRare={ rareCardSelect }
          cardTrunfo={ isTrunfo }
          onInputChange={ this.handleChange }
        />
        <Card />
      </>
    );
  }
}

export default App;
